import React, { useState, useEffect } from "react";
import { getAllClasses, createClass } from "../Common/ClassService";
import ClassList from "./ClassList";
import CreateClassForm from "./CreateClassForm";

const Main = () => {
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = () => {
    setLoading(true);
    getAllClasses()
      .then((results) => setClasses(results || []))
      .catch((err) => console.error("Error fetching classes:", err))
      .finally(() => setLoading(false));
  };

  const handleCreateClass = (name, description) => {
    createClass(name, description)
      .then(() => fetchClasses())
      .catch((err) => alert("Error creating class: " + err.message))
      .finally(() => setShowForm(false));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Class Management</h1>

      {showForm ? (
        <CreateClassForm
          onCreate={handleCreateClass}
          onCancel={() => setShowForm(false)}
        />
      ) : (
        <button
          onClick={() => setShowForm(true)}
          style={{
            marginBottom: "20px",
            padding: "10px 20px",
            backgroundColor: "#008CBA",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Add New Class
        </button>
      )}

      {loading ? (
        <p>Loading classes...</p>
      ) : (
        <ClassList classes={classes} />
      )}
    </div>
  );
};

export default Main;
