import React from "react";
import { Link } from "react-router-dom";

const ClassList = ({ classes }) => {
  if (!classes.length) {
    return <p>No classes available. Click “Add New Class” to create one.</p>;
  }

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
        gap: "20px",
      }}
    >
      {classes.map((classObj) => (
        <div
          key={classObj.id}
          style={{
            padding: "15px",
            border: "1px solid #ddd",
            borderRadius: "5px",
            backgroundColor: "#f9f9f9",
          }}
        >
          <h3>{classObj.get("Name")}</h3>
          <p>{classObj.get("Description") || "No description available"}</p>
          <p style={{ fontSize: "12px", color: "#666" }}>ID: {classObj.id}</p>
          <Link
            to={`/students?classId=${classObj.id}`}
            style={{ color: "#008CBA", textDecoration: "none" }}
          >
            View Students →
          </Link>
        </div>
      ))}
    </div>
  );
};

export default ClassList;
